import {useNavigation} from '@react-navigation/native';
import React, {useState} from 'react'
import {View, Image, StyleSheet, Text, Dimensions, TouchableHighlight} from 'react-native';
import {Button} from "react-native-paper";


const imageWidth = Dimensions.get("screen").width;
const imageHeight = imageWidth;




export default function OnboardingScreen1() {
    const navigation = useNavigation();


    return (
        <View>
            <Image style={styles.image} source={require('../photos/onboarding/onboard_1.jpg')}/>

            <View style={styles.container}>
                <Text style={styles.title}> Автоматична Система Часу </Text>
                <View style={styles.textWrapper}>
                    <Text style={styles.text}> Тестуйте спортивні навички за допомогою воріт і
                        зручно організовуйте власні тренування </Text>
                </View>

                <View style={styles.buttonContainer}>
                    <Button
                        style={styles.button}
                        variant="outlined"
                        onPress={() => {
                            navigation.navigate("SignUp")
                        }}
                    >
                      <Text  style={{color: "#555555", fontSize: 18}}> Пропустити </Text>
                    </Button>
                    <Button
                        style={styles.button}
                        variant="outlined"
                        onPress={
                            () => navigation.navigate("OnboardingScreen2")
                        }>
                        <Text style={{color: "#555555", fontSize: 18}}>Далі {'→'} </Text>
                    </Button>


                </View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        // flex:1,
        height: Dimensions.get('screen').height - imageHeight,
    },
    title: {
        marginTop: '10%',
        fontSize: 25,
        textAlign: "center",
        flex: 1,
    },
    text: {
        marginTop: '5%',
        fontSize: 18,
        width: '70%',
        textAlign: "center",
    },
    textWrapper: {
        flex: 3,
        // marginBottom: '20%',
        justifyContent: 'center',
        alignItems: 'center',
    },
    image: {
        height: imageHeight,
        width: imageWidth,
    },
    buttonContainer: {
        flex: 3,
        // marginBottom: "15%",
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
    },
})
